CREATE TABLE `orders`.`order_tbl` (`id` INT NOT NULL AUTO_INCREMENT , `order_name` VARCHAR(50) NOT NULL , `order_category` VARCHAR(50) NOT NULL , `order_rating` INT(11) NOT NULL DEFAULT '0' , `order_img` VARCHAR(225) NOT NULL , `order_price` INT(11) NOT NULL DEFAULT '0' , `discount_rate` INT(11) NOT NULL DEFAULT '0' , `discount_price` INT(11) NOT NULL DEFAULT '0' , PRIMARY KEY (`id`))